
public class Assignment_comp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 //Difference of two numbers
		byte a = 5;
		byte b = 3;
		byte Difference = (byte) (a - b);
		System.out.println("The difference of 5 and 3 is: "+Difference);
		
		//Product of three numbers
		byte x = 5;
		byte y = 6;
		byte z = 7;
		short Product = (short) (x*y*z);
		System.out.print("The product of 5,6 and 7 is: "+Product);
	}

}
